import React from 'react';
import stylesLogin from '../styles/login_css';
import { View, TextInput, Image, Text} from 'react-native';
import * as Font from 'expo-font';
// import { useNavigation } from '@react-navigation/native';
import { Button } from 'react-native-elements';

class Login extends React.Component {

    state={
        user: '',
        password: '',
        isButtonLoading: false,
        fontsLoaded: false,
    };

    componentDidMount(){
        this.loadFonts();
    }


    async loadFonts(){
        await Font.loadAsync({
            'Raleway-Light':{
                uri: require('../assets/fonts/Raleway-Light.ttf')
            },
            'Raleway-Medium':{
                uri: require('../assets/fonts/Raleway-Medium.ttf')
            },
            'Roboto-Regular':{
                uri: require('../assets/fonts/Roboto-Regular.ttf')
            }
        });
        this.setState({
            fontsLoaded: true
        });
    }
    
    buttonClicked(){
        this.setState({isButtonLoading: true});
        this.props.navigation.navigate('Home');
    }
    
    render(){
        if(this.state.fontsLoaded){
            return(
                <View style={stylesLogin.login}>
                    <Image source={require('../assets/logo1.png')} style={stylesLogin.logo}></Image>
                    <Text style={stylesLogin.brand}>My Plug</Text>
                    <TextInput style={stylesLogin.textinputUser} placeholder=" User"></TextInput>
                    <TextInput style={stylesLogin.textinputPassword} placeholder=" Password"></TextInput>
                    <View style={stylesLogin.buttonLogin}>
                        <Button title="Log in" type="solid" loading={this.state.isButtonLoading} onPress={() => this.buttonClicked()}></Button>
                    </View>
                </View>
            )
        }else{
            return null;
        }
    }
}
export default Login
